export const initialState = {
	versions: [],
	selectedVersion: null,
	isLoading: true,
};

export default function reducer(state, action) {
	const pState = { ...state };

	if (action.type === 'DATA_FROM_SERVER') {
		//setSelectedStep(response.data[0].stepsCount + 1);
		console.log(response.data[0]);
		//setSelectedVersion(response.data[0]);

		//const latest = response.data.sort((a, b) => b.createdAt - a.createdAt);

		const recentVersions = response.data.sort(
			(a, b) => new Date(b.createdAt) - new Date(a.createdAt)
		);

		const latestInProgress = recentVersions.find((v) => {
			return v.status === 'in_progress';
		});

		//console.log(latestInProgress.stepsCount);

		pState.selectedVersion = { ...latestInProgress };
	}
}
