export const initialState = {
	goals: [],
};

export const ACTIONS = {
	ON_NEW_GOAL: 'on_new_goal',
};
export default function redeucer(state, action) {
	const pState = { ...state };

	if (action.type === ACTIONS.ON_NEW_GOAL) {
		pState.goals = [...pState.goals, action.payload.newGoal];
	}
	return pState;
}
