import React, { useState } from 'react';
import classes from './StepFour.module.css';
import uniqueId from 'lodash.uniqueid';

import Tag from '../../UI/Tag';

import { DEFAULT_TAGS_DATA } from '../../../../data/step-four/defaultData';
import StepFourStatic from './StepFourStatic';
import AddNewTag from './AddNewTag';

const StepFour = () => {
	let [tags, setTags] = useState(DEFAULT_TAGS_DATA);
	const [selectedTags, setSelectedTags] = useState([]);
	const [tagTitle, setTagTitle] = useState('');

	const addToSelected = (item) => {
		if (item.status === 'selected') {
			setSelectedTags((prevTags) => {
				return [...prevTags, item];
			});
		} else {
			let prevTags = selectedTags;
			const filteredTags = prevTags.filter((tag) => tag.id !== item.id);
			setSelectedTags(filteredTags);
			console.log(filteredTags.length);
		}
	};

	const newTagHandler = () => {
		if (tagTitle.trim() === '') {
			return;
		}
		let newId = uniqueId();
		const ITEM = { id: newId, title: tagTitle, status: 'not_selected' };
		setTags((prevTags) => {
			return [...prevTags, ITEM];
		});
		setTagTitle('');
	};
	return (
		<section className={classes.stepFourOfEleven}>
			<StepFourStatic />
			{/* <div className={classes['text-area-wrapper']}>
				<textarea
					className={classes['selectedArea']}
					onChange={(event)=>setTagTitle(event.currentTarget.value)}
					value={tagTitle}
				></textarea>
				<div className={classes['add-button']}>
					<div className={classes['plus-icon']} onClick={newTagHandler}>
						+
					</div>{' '}
					<div>Add</div>
				</div>
			</div> */}

			<AddNewTag
				onNewTag={newTagHandler}
				onTitle={(event) => setTagTitle(event.currentTarget.value)}
				tagTitle={tagTitle}
			/>

			<br />
			<div className={classes.selectedTextArea}>
				{tags.length > 0 ? (
					tags.map((obj, i) => (
						<Tag
							key={obj.id}
							title={obj.title}
							id={obj.id}
							onClick={addToSelected}
						/>
					))
				) : (
					<div></div>
				)}
			</div>
		</section>
	);
};

export default StepFour;
