import React, { useState, useEffect, useContext, useReducer } from 'react';
import { getIssues } from '../../../../api/steptwo';
import AuthContext from '../../../../store/auth-context';
import NewVersionContext from '../../../../store/new-version-context';

import './StepTwo.css';
import Feeling from './Feeling.js';
import FeelingCard from './FeelingCard.js';

import StepTwoStatic from './StepTwoStatic';

const FEELING_CARDS = [
	{
		title: 'Low Impact',
		id: 'low-impact',
		feelings: [{ issueTitle: 'low impact', issueImpactType: 'low', id: '1' }],
	},
	{
		title: 'Medium Impact',
		id: 'medium-impact',
		feelings: [
			{ issueTitle: 'medium impact', issueImpactType: 'medium', id: '2' },
		],
	},
	{
		title: 'High Impact',
		id: 'high-impact',
		feelings: [{ issueTitle: 'high impact', issueImpactType: 'high', id: '3' }],
	},
	{
		title: 'Critical Impact',
		id: 'critical-impact',
		feelings: [
			{ issueTitle: 'critical impact', issueImpactType: 'critical', id: '4' },
		],
	},
];

const issues = {
	low: [{ issueTitle: 'low impact', issueImpactType: 'low', id: '1' }],
	medium: [{ issueTitle: 'medium impact', issueImpactType: 'medium', id: '2' }],
	high: [{ issueTitle: 'high impact', issueImpactType: 'high', id: '3' }],
	critical: [
		{ issueTitle: 'critical impact', issueImpactType: 'critical', id: '4' },
	],
};

const ACTTIONS = {
	DROP_ON_LOW: 'drop_on_low',
	DROP_ON_MEDIUM: 'drop_on_medium',
	DROP_ON_HIGH: 'drop_on_high',
	DROP_ON_CRITICAL: 'drop_on_critical',
};
function reducer(state, action) {
	return state;
}

// actual functoin definition starts here
const StepTwo = (props) => {
	const [state, dispatch] = useReducer(reducer, issues);

	const authCtx = useContext(AuthContext);
	const newVerCtx = useContext(NewVersionContext);

	const [feelingCards, setFeelingCards] = useState(FEELING_CARDS);

	const [firstFeelings, setFirstFeelings] = useState(FEELING_CARDS[0].feelings);
	const [secondFeelings, setSecondFeelings] = useState(
		FEELING_CARDS[1].feelings
	);
	const [thirdFeelings, setThirdFeelings] = useState(FEELING_CARDS[2].feelings);
	const [fourthFeelings, setFourthFeelings] = useState(
		FEELING_CARDS[3].feelings
	);

	///////////////////////// DRAG & DROP FUNCTIONALITY HERE /////////////////////
	const [draggingFeeling, setDraggingFeeling] = useState(null);

	const [dragSourceId, setDragSourceId] = useState(null);

	////////////////////////////////////// HELPER FUNCTIONS       /////////////////////
	const cleanSourceCard = (id, draggingThg) => {
		if (id == 1) {
			const filtered = firstFeelings.filter((thg) => thg.id != draggingThg.id);
			setFirstFeelings(filtered);
		} else if (id == 2) {
			const filtered = secondFeelings.filter((thg) => thg.id != draggingThg.id);
			setSecondFeelings(filtered);
		} else if (id == 3) {
			const filtered = thirdFeelings.filter((thg) => thg.id != draggingThg.id);
			setThirdFeelings(filtered);
		} else if (id == 4) {
			const filtered = fourthFeelings.filter((tgh) => tgh.id != draggingThg.id);
			setFourthFeelings(filtered);
		}
	};

	const findInFirstCard = (draggingTgh) => {
		const found = firstFeelings.find((thg) => thg.id == draggingTgh.id);
		if (found) {
			return true;
		}
	};

	const findInSecondCard = (draggingTgh) => {
		const found = secondFeelings.find((thg) => thg.id == draggingTgh.id);
		if (found) {
			return true;
		}
	};

	const findInThirdCard = (draggingTgh) => {
		const found = thirdFeelings.find((thg) => thg.id == draggingTgh.id);
		if (found) {
			return true;
		}
	};

	const findInFourthCard = (draggingTgh) => {
		const found = fourthFeelings.find((thg) => thg.id == draggingTgh.id);
		if (found) {
			return true;
		}
	};

	const dragStartHandler = (event) => {
		console.log('drag is started', event.target.parentNode.id);
		event.target.classList.add('dragging');

		if (event.target.parentNode.id == 4) {
			setDraggingFeeling(event.target.id);
			setDragSourceId(4);
		}
		if (event.target.parentNode.id == 3) {
			setDraggingFeeling(event.target.id);
			setDragSourceId(3);
		}
		if (event.target.parentNode.id == 2) {
			setDraggingFeeling(event.target.id);
			setDragSourceId(2);
		}
		if (event.target.parentNode.id == 1) {
			setDraggingFeeling(event.target.id);
			setDragSourceId(1);
		}
	};

	const dragEndHandler = (event) => {
		event.target.classList.remove('dragging');
	};

	const dropHandler = (event) => {
		event.preventDefault();
		newVerCtx.stepOneUnSaver();

		if (event.target.id == 1) {
			if (dragSourceId == 1) {
				return;
			}

			dispatch({
				type: ACTTIONS.DROP_ON_LOW,
				payload: { id: draggingFeeling },
			});
		} else if (event.target.id == 2) {
			if (dragSourceId == 2) {
				return;
			}
			dispatch({
				type: ACTTIONS.DROP_ON_MEDIUM,
				payload: { id: draggingFeeling },
			});
		} else if (event.target.id == 3) {
			if (dragSourceId == 3) {
				return;
			}

			dispatch({
				type: ACTTIONS.DROP_ON_HIGH,
				payload: { id: draggingFeeling },
			});
		} else if (event.target.id == 4) {
			if (dragSourceId == 4) {
				return;
			}
			dispatch({
				type: ACTTIONS.DROP_ON_CRITICAL,
				payload: { id: draggingFeeling },
			});
		}

		// const issues = {
		// 	low: firstFeelings,
		// 	medium: secondFeelings,
		// 	high: thirdFeelings,
		// 	critical: fourthFeelings,
		// };
		// newVerCtx.stepTwoIssuesModifier(issues);
	};

	const allowDrop = (event) => {
		console.log('dragging over element');
		event.preventDefault();
	};

	////////////////////////// END DRAG & DROP FUNCTIONALITY HERE ////////////

	useEffect(() => {
		// if (newVerCtx.stepOneSaved) {
		// 	setFirstFeelings((prev) => {
		// 		return [
		// 			{
		// 				id: '1',
		// 				issueTitle: newVerCtx.stepOneFirst,
		// 				issueImpactType: 'low',
		// 			},
		// 		];
		// 	});
		// 	setSecondFeelings((prev) => {
		// 		return [
		// 			{
		// 				id: '2',
		// 				issueTitle: newVerCtx.stepOneSecond,
		// 				issueImpactType: 'medium',
		// 			},
		// 		];
		// 	});

		// 	setThirdFeelings((prev) => {
		// 		return [
		// 			{
		// 				id: '3',
		// 				issueTitle: newVerCtx.stepOneThird,
		// 				issueImpactType: 'high',
		// 			},
		// 		];
		// 	});
		// 	setFourthFeelings([]);
		// }

		// for stepTwo Issues in NewVersionContext

		(async function () {
			let response = await getIssues(newVerCtx.versionId);
			//console.log(response.data[0].issueImpactType[0]);

			if (response.success && response.data.length > 0) {
				response.data.map((issue) => {
					if (issue.issueImpactType[0] === 'low') {
						setFirstFeelings((prev) => [issue]);
					} else if (issue.issueImpactType[0] === 'medium') {
						setSecondFeelings((prev) => [issue]);
					} else if (issue.issueImpactType[0] === 'high') {
						setThirdFeelings((prev) => [issue]);
					} else if (issue.issueImpactType[0] === 'critical') {
						setFourthFeelings((prev) => [issue]);
					}
				});

				console.log(response.data);
			}
		})();
		const issues = {
			low: firstFeelings,
			medium: secondFeelings,
			high: thirdFeelings,
			critical: fourthFeelings,
		};
		newVerCtx.stepTwoIssuesModifier(issues);
	}, []);

	return (
		<section className='stepTwoOfEleven'>
			<StepTwoStatic />

			<div className='feelings-cards-container'>
				<div className='feeling-card' id='1'>
					<div className='feeling-card__title'>{feelingCards[0].title}</div>
					<FeelingCard
						id='1'
						key={1}
						feelings={issues.low}
						onDragStart={dragStartHandler}
						onDragEnd={dragEndHandler}
						onDragOver={allowDrop}
						onDrop={dropHandler}
					/>
				</div>
				<div className='feeling-card' id='2'>
					<div className='feeling-card__title'>{feelingCards[1].title}</div>
					<FeelingCard
						id='2'
						key={2}
						feelings={issues.medium}
						onDragStart={dragStartHandler}
						onDragEnd={dragEndHandler}
						onDragOver={allowDrop}
						onDrop={dropHandler}
					/>
				</div>

				<div className='feeling-card' id='3'>
					<div className='feeling-card__title'>{feelingCards[2].title}</div>
					<FeelingCard
						id='3'
						key={3}
						feelings={issues.high}
						onDragStart={dragStartHandler}
						onDragEnd={dragEndHandler}
						onDragOver={allowDrop}
						onDrop={dropHandler}
					/>
				</div>

				<div className='feeling-card' id='4'>
					<div className='feeling-card__title'>{feelingCards[3].title}</div>
					<FeelingCard
						id='4'
						key={4}
						feelings={issues.critical}
						onDragStart={dragStartHandler}
						onDragEnd={dragEndHandler}
						onDragOver={allowDrop}
						onDrop={dropHandler}
					/>
				</div>
			</div>
		</section>
	);
};

export default StepTwo;
