import React, { useEffect, useReducer, useRef, useState } from 'react';

import VisionBoardCard from './VisionBoardCard';
import classes from './VisionBoard.module.css';

import reducer, { ACTIONS, initialState } from './reducer';
const VisionBoard = ({ onStateChange }) => {
	let currentYear = new Date().getFullYear();

	const [state, dispatch] = useReducer(reducer, initialState);

	let fiveYears = {
		currentYear: currentYear,
		currentYearFirstNext: currentYear + 1,
		currentYearSecondNext: currentYear + 2,
		currentYearThirdNext: currentYear + 3,
		currentYearFourthNext: currentYear + 4,
	};

	useEffect(() => {
		console.log('in vision board', state.goals);
	}, [state]);

	return (
		<div className={classes['vision-board']}>
			<div className='row'>
				<div className='col no-padding' style={{ padding: '0' }}>
					<VisionBoardCard
						year={fiveYears.currentYear}
						onNewGoal={(goal) =>
							dispatch({
								type: ACTIONS.ON_NEW_GOAL,
								payload: { newGoal: goal },
							})
						}
						goals={state.goals.filter((g) => g.year === fiveYears.currentYear)}
					/>
				</div>
				<div className='col no-padding' style={{ padding: '0' }}>
					<VisionBoardCard
						year={fiveYears.currentYearFirstNext}
						onNewGoal={(goal) =>
							dispatch({
								type: ACTIONS.ON_NEW_GOAL,
								payload: { newGoal: goal },
							})
						}
						goals={state.goals.filter(
							(g) => g.year === fiveYears.currentYearFirstNext
						)}
					/>
				</div>
				<div className='col' style={{ padding: '0' }}>
					<VisionBoardCard
						year={fiveYears.currentYearSecondNext}
						onNewGoal={(goal) =>
							dispatch({
								type: ACTIONS.ON_NEW_GOAL,
								payload: { newGoal: goal },
							})
						}
						goals={state.goals.filter(
							(g) => g.year === fiveYears.currentYearSecondNext
						)}
					/>
				</div>
			</div>
			<div className='row'>
				<div className='col' style={{ padding: '0' }}>
					<VisionBoardCard
						year={fiveYears.currentYearThirdNext}
						onNewGoal={(goal) =>
							dispatch({
								type: ACTIONS.ON_NEW_GOAL,
								payload: { newGoal: goal },
							})
						}
						goals={state.goals.filter(
							(g) => g.year === fiveYears.currentYearThirdNext
						)}
					/>
				</div>
				<div className='col' style={{ padding: '0' }}>
					<VisionBoardCard
						year={fiveYears.currentYearFourthNext}
						onNewGoal={(goal) =>
							dispatch({
								type: ACTIONS.ON_NEW_GOAL,
								payload: { newGoal: goal },
							})
						}
						goals={state.goals.filter(
							(g) => g.year === fiveYears.currentYearFourthNext
						)}
					/>
				</div>
				<div className='col' style={{ padding: '0' }}></div>
			</div>
		</div>
	);
};

export default VisionBoard;
