import { faBirthdayCake } from '@fortawesome/free-solid-svg-icons';
import uniqueId from 'lodash.uniqueid';
import { DEFAULT_TAGS_DATA } from '../../../../data/step-four/defaultData';
export const INITIAL_STATE = {
	tags: DEFAULT_TAGS_DATA,
	firstTags: DEFAULT_TAGS_DATA,
	secondTags: DEFAULT_TAGS_DATA,
	thirdTags: DEFAULT_TAGS_DATA,
	tagTitle: '',
	dataIsLoading: true,
};

export const ACTIONS = {
	CHANGE_TITLE: 'title_change',
	TOGGLE_SELECT: 'toggle_select',
	ADD_NEW_TAG: 'add_new_tag',
	DATA_FROM_SERVER: 'data_from_server',
	DATA_FROM_LOCAL_STATE: 'data_from_local_state',
};

export default function reducer(state, action) {
	const type = action.type;
	const data = action.payload.data;

	const prevState = { ...state };

	switch (type) {
		case ACTIONS.ADD_NEW_TAG:
			let newId = uniqueId();
			const newTag = {
				id: newId,
				title: prevState.tagTitle,
				status: 'not_selected',
			};
			prevState.tags = [...prevState.tags, newTag];

			switch (newTag.tagCategory) {
				case 'rules':
					prevState.firstTags = [...prevState.firstTags, newTag];
					break;
				case 'breakthroughs':
					prevState.secondTags = [...prevState.secondTags, newTag];
					break;
				case 'realizations':
					prevState.thirdTags = [...prevState.thirdTags, newTag];

				default:

				// do nothing
			}
			prevState.tagTitle = '';
			break;
		case ACTIONS.CHANGE_TITLE:
			prevState.tagTitle = data;
			break;
		case ACTIONS.TOGGLE_SELECT:
			if (data.status === 'selected') {
				prevState.tags.map((tag) => {
					if (tag.id == data.id) {
						tag['status'] = 'not_selected';
					}
				});
			} else {
				prevState.tags.map((tag) => {
					if (tag.id == data.id) {
						tag['status'] = 'selected';
					}
				});
			}

			break;
		case ACTIONS.DATA_FROM_SERVER:
			//console.log(action);
			prevState['tags'] = data;
			prevState.tags.map((tag) => {
				tag['id'] = tag._id;
			});
			prevState.dataIsLoading = false;
			break;
		case ACTIONS.DATA_FROM_LOCAL_STATE:
			prevState.tags = [
				{
					id: '123',
					tagTitle: 'Watch a Comedy',
					status: 'selected',
					tagCategory: '',
				},
				{ id: '453', title: 'Go to sleep early', status: 'not_selected' },
				{
					id: '323',
					tagTitle: 'Weighted blanket',
					status: 'not_selected',
					tagCategory: '',
				},
				{
					id: '562',
					tagTitle: 'Go to a movie',
					status: 'not_selected',
					tagCategory: '',
				},
				{
					id: '531',
					tagTitle: 'Ride a bike',
					status: 'not_selected',
					category: '',
				},
			];

			prevState.firstTags = [...prevState.tags];
			prevState.secondTags = [...prevState.tags];
			prevState.thirdTags = [...prevState.tags];
			prevState.firstTags.map((tag) => {
				tag['tagCategory'] = 'rules';
			});

			prevState.secondTags.map((tag) => {
				tag['tagCategory'] = 'breakthroughs';
			});
			prevState.thirdTags.mao((tag) => {
				tag['tagCategory'] = 'realizations';
			});
			prevState.dataIsLoading = false;
			break;
		default:
		//return prevState;
	}
	return prevState;
}
